mapper.update:
update users
username = #{username}, name = #{name}, gender = #{gender}, role = #{role}, identifier = #{identifier}, subject = #{subject}, update_time = #{updateTime}
where id = #{id}

mapper.select:
select id, username, password, name, gender, role, identifier, subject, create_time, update_time from users

    role != 3

        AND name like concat('%',#{name},'%')


        AND gender = #{gender}


        AND role = #{role}


        AND subject = #{subject}
